package com.zlhx.makeSql.excel;import org.apache.poi.hssf.usermodel.*;/** * Excel Row工具类  * @author liuxue * @date 2013-6-8 */class ExcelSheetRow {		    /** 	     * 创建当前标题行 	     * @param sheet 	     * @return 	     */  	    public static HSSFRow createCurrSheetTitle(ExcelWorkBook work) {  	       HSSFSheet sheet = work.getSheet();	       return sheet.createRow(0);	    }  	      	    /** 	     * 创建当前excel记录内容 	     * @param sheet 	     * @param i 	     * @return 	     */  	    public static HSSFRow createCurrSheetRecord(ExcelWorkBook work,int i) {  	        HSSFSheet sheet = work.getSheet();	        return sheet.createRow(i+1);	    }   	  	     }